import React, { useEffect, useState } from 'react';

const TagForm = () => {
  const [currentTag, setCurrentTag] = useState('Loading...');
  const [currentName, setCurrentName] = useState('');
  const [timestamp, setTimestamp] = useState('Loading...');

  useEffect(() => {
    const socket = new WebSocket("ws://localhost:8082/"); // Lembre-se de ajustar o endereço IP conforme necessário

    socket.onopen = function(event) {
      console.log("Conectado ao servidor WebSocket");
    };

    socket.onmessage = function(event) {
      const data = JSON.parse(event.data);

      if (data.tag === 'requestName') {
        // Se o servidor solicitar um nome, não fazemos nada aqui
        // pois o formulário de registro já deve ter sido preenchido anteriormente
      } else {
        setCurrentTag(`Current RFID Tag: ${data.tag}`);
        setCurrentName(`Name: ${data.name}`);
        if (data.timestamp) {
          setTimestamp(`Timestamp: ${data.timestamp}`);
        }
      }
    };

    socket.onclose = function(event) {
      console.log("Conexão fechada");
    };

    return () => {
      // Fechar a conexão do WebSocket ao desmontar o componente
      socket.close();
    };
  }, []); // A lista vazia de dependências garante que este efeito só é executado uma vez

  const showRegistrationForm = () => {
    const tagNameElement = document.getElementById("tagName");
    const name = tagNameElement.value;

    // Enviar o nome associado à tag RFID
    // Note que este envio depende de como o servidor espera receber a informação
    // Pode ser necessário ajustar conforme necessário
    // socket.send(`${name}`);
  };

  return (
    <div>
      <h1>RFID Tags Viewer</h1>
      <p id="currentTag">{currentTag}</p>
      <p id="currentName">{currentName}</p>
      <p id="timestamp">{timestamp}</p>

      <form id="registrationForm" style={{ display: 'none' }} onSubmit={showRegistrationForm}>
        <label htmlFor="tagName">Tag Name:</label>
        <input type="text" id="tagName" />
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default TagForm;
