import React, { useState } from 'react';
import  useWebSocket from 'react-use-websocket';
import axios from 'axios';

const TagForm = () => {
  const [tagInput, setTagInput] = useState('');
  const socketUrl = 'ws://localhost:8082'; // Substitua pelo URL real do seu servidor WebSocket
  const { sendMessage } = useWebSocket(socketUrl);

  const handleInputChange = (event) => {
    setTagInput(event.target.value);
  };

  const sendTag = async () => {
    try {
      if (tagInput.trim() !== '') {
        // Enviar a tag para o servidor via WebSocket
        sendMessage({ data: JSON.stringify({ tag: tagInput }) });
        console.log('Sent tag via WebSocket:', tagInput);
      }
    } catch (error) {
      console.error('Erro ao processar dados:', error);
    }
  };

  // Assim que receber a mensagem do WebSocket, atualizar o estado com a tag recebida
  const handleWebSocketMessage = async (message) => {
    const data = JSON.parse(message.data);
    console.log('Received message from WebSocket:', data);
    setTagInput(data.tag);

    // Chamar a API POST
    try {
      const response = await axios.post('http://localhost:8082/api/tags', { tag: data.tag });
      console.log('Sent tag via POST:', data.tag);
      console.log('API Response:', response.data);
    } catch (error) {
      console.error('Erro ao chamar a API POST:', error);
    }
  };

  // Configurar o hook useWebSocket com a função de callback para mensagens
  useWebSocket(socketUrl, { onMessage: handleWebSocketMessage });

  return (
    <div>
      <h1>Create Tag (React)</h1>
      <form>
        <label htmlFor="tagInput">Tag:</label>
        <input
          type="text"
          id="tagInput"
          value={tagInput}
          onChange={handleInputChange}
          required
        />
        <button type="button" onClick={sendTag}>
          Create Tag
        </button>
      </form>
    </div>
  );
};

export default TagForm;
