import React, { useState, useEffect } from 'react';
import axios from 'axios';
import useWebSocket from 'react-use-websocket';
import "../index.css";

const TagForm = () => {
  const [currentTag, setCurrentTag] = useState('Loading...');
  const [name, setName] = useState('');
  const [cpf, setCpf] = useState('');
  const [savedMessage, setSavedMessage] = useState('');

  const socketUrl = 'ws://localhost:8082/';
  const { lastMessage} = useWebSocket(socketUrl);

  useEffect(() => {
    if (lastMessage !== null) {
      const data = JSON.parse(lastMessage.data);
      setCurrentTag(data.tag);
    }
  }, [lastMessage]);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleCpfChange = (e) => {
    setCpf(e.target.value);
  };

  const handleSave = async () => {
    try {
      // Fazer uma chamada para o endpoint "/associate-tag" no backend usando Axios
      await axios.post('http://localhost:8082/associate-tag/', {
        tag: currentTag,
        name: name,
        cpf: cpf,
      });

      // Exibir mensagem de sucesso para o usu치rio
      setSavedMessage('Tag associated and saved in the database!');
    } catch (error) {
      // Se houver um erro, exibir a mensagem de erro ou lidar com ele conforme necess치rio
      console.error('Error processing data:', error);
      setSavedMessage('Error saving tag in the database.');
    }
  };

  return (
    <div >
      <h1>RFID Tags Viewer</h1>
      <p id="currentTag">Current RFID Tag: {currentTag}</p>

      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={handleNameChange}
        />
      </div>

      <div>
        <label htmlFor="cpf">CPF:</label>
        <input
          type="text"
          id="cpf"
          value={cpf}
          onChange={handleCpfChange}
        />
      </div>

      <button onClick={handleSave}>Save Tag</button>

      {/* Span para mostrar a mensagem de sucesso ou erro */}
      {savedMessage && <span>{savedMessage}</span>}
    </div>
  );
};

export default TagForm;
