// WebSocketTag.js
import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:8082'); // Substitua pela URL real do seu servidor

const TagForm = () => {
  const [tag, setTag] = useState('');

  useEffect(() => {
    // Configurar o listener para a tag
    socket.on('tag', (data) => {
      setTag(data.tag);
    });

    return () => {
      // Desconectar o socket ao desmontar o componente
      socket.disconnect();
    };
  }, []);

  return (
    <div>
      <h1>WebSocket Tag:</h1>
      <p>{tag}</p>
    </div>
  );
};

export default TagForm;
