// TagViewer.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';

const TagViewer = () => {
  const [tags, setTags] = useState([]);
  const [selectedTag, setSelectedTag] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  useEffect(() => {
    // Obtenha a lista de tags da sua API ou fonte de dados
    axios.get('http://localhost:8082/tags/s')
      .then(response => {
        setTags(response.data);
      })
      .catch(error => {
        console.error('Error fetching tags:', error);
      });
  }, []);

  const openModal = (tag) => {
    setSelectedTag(tag);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setSelectedTag(null);
    setModalIsOpen(false);
  };

  return (
    <div>
      <h2>Tags Cadastradas</h2>
      <ul>
        {tags.map(tag => (
          <li key={tag.id} onClick={() => openModal(tag)}>
            {tag.name}
          </li>
        ))}
      </ul>

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Tag Information Modal"
      >
        <h2>Informações da Tag</h2>
        {selectedTag && (
          <div>
            <p>ID: {selectedTag.id}</p>
            <p>Name: {selectedTag.name}</p>
            {/* Adicione outras informações aqui conforme necessário */}
          </div>
        )}
        <button onClick={closeModal}>Fechar Modal</button>
      </Modal>
    </div>
  );
};

export default TagViewer;
