import React, { useState, useEffect } from 'react';
import useWebSocket from 'react-use-websocket';
import axios from 'axios';

const TagForm = () => {
  const [currentTag, setCurrentTag] = useState('Loading...');
  const [savedMessage, setSavedMessage] = useState('');

  const socketUrl = 'ws://localhost:8082/';
  const { lastMessage } = useWebSocket(socketUrl);

  useEffect(() => {
    if (lastMessage !== null) {
      const data = JSON.parse(lastMessage.data);
      setCurrentTag(data.tag);
    }
  }, [lastMessage]);

  const handleSave = async () => {
    try {
      // Fazer uma chamada para o endpoint "/associate-tag" no backend usando Axios
      const response = await axios.post('http://localhost:8082/associate-tag/', {
        tag: currentTag,
      });
      // Exibir mensagem de sucesso para o usuário
      setSavedMessage('Tag saved in the database!');
    } catch (error) {
      setSavedMessage('Error saving tag in the database.');
    }
  };

  return (
    <div>
      <h1>RFID Tags Viewer</h1>
      <p id="currentTag">Current RFID Tag: {currentTag}</p>

      <button onClick={handleSave}>Save Tag</button>
      {savedMessage && <span>{savedMessage}</span>}
    </div>
  );
};

export default TagForm;
