import React, { useState, useEffect } from 'react';
import useWebSocket  from 'react-use-websocket';

const TagForm = () => {
  const [tagName, setTagName] = useState('');
  const [registrationFormVisible, setRegistrationFormVisible] = useState(false);
  const [currentTag, setCurrentTag] = useState('');


  const { lastJsonMessage, sendMessage } = useWebSocket('ws://localhost:8082', {
    onOpen: () => console.log(`Connected to App WS`),
    onMessage: () => {
      if (lastJsonMessage) {
        console.log(lastJsonMessage);
      }
    },
    queryParams: { 'token': '123456' },
    onError: (event) => { console.error(event); },
    shouldReconnect: (closeEvent) => true,
    reconnectInterval: 3000
  });

  const handleRegistrationSubmit = (event) => {
    event.preventDefault();
    sendMessage(`${tagName}`);
    setRegistrationFormVisible(false);
  };

  // Effect to handle incoming WebSocket messages
  useEffect(() => {
    if (lastJsonMessage) {
      const data = lastJsonMessage;
      if (data.tag === 'requestName') {
        setRegistrationFormVisible(true);
      } else {
        setCurrentTag(`Current RFID Tag: ${data.tag}`);
      }
      
    }
    
  }, [lastJsonMessage]);
 

  return (
    <div>
      <h1>RFID Tags Viewer</h1>

    
      <p id="currentTag">{currentTag}</p>
    </div>
  );
};

export default TagForm;
