import React, { useState, useEffect } from "react";
import axios from "axios";
import useWebSocket from "react-use-websocket";

const EnterLeft = () => {
  const [id_user, setIdUser] = useState("");
  const [tag, setTag] = useState("");
  const [isCardRead, setIsCardRead] = useState(false);
  const [showTag, setShowTag] = useState(false);

  const socketUrl = "ws://localhost:8082/";
  const { lastMessage } = useWebSocket(socketUrl);

  useEffect(() => {
    if (lastMessage !== null) {
      const data = JSON.parse(lastMessage.data);
      setIdUser(data.id_user);
      setTag(data.tag);

      // A leitura do cartão ocorreu apenas se o valor de "tag" for diferente
      // do valor anterior.
      if (data.tag !== tag) {
        setIsCardRead(true);
        setShowTag(true);

        // Define um temporizador para esconder a tag após 10 segundos
        setTimeout(() => {
          setShowTag(false);
          setIsCardRead(false);
        }, 10000);
      }
    }
  }, [lastMessage, tag]);

  useEffect(() => {
    const sendRequest = async () => {
      try {
        const response = await axios.put("http://localhost:8082/register/", { tag });
        console.log(response.data);
      } catch (error) {
        console.error("Error:", error);
      }
    };

    if (isCardRead) {
      sendRequest();
    }
  }, [isCardRead, tag]);

  const handleRegisterEntry = async () => {
    try {
      const response = await axios.post("http://localhost:8082/register/", {
        id_user,
        tag,
      });
      console.log(response.data);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleRegisterExit = async () => {
    try {
      const response = await axios.patch("http://localhost:8082/register/", {
        id_user,
        tag,
      });

      if (response.data.success) {
        console.log(response.data);
      } else {
        console.error("Error:", response.data.message);
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div>
      <h2>Registrar Entrada</h2>
      {/* ... Seu restante do código ... */}
      {showTag && (
        <div>
          <label>Tag Lida:</label>
          <span>{tag}</span>
        </div>
      )}

      <button onClick={handleRegisterEntry}>Registrar Entrada</button>

      <hr />

      <h2>Registrar Saída</h2>
      {/* ... Seu restante do código ... */}
      <button onClick={handleRegisterExit}>Registrar Saída</button>
    </div>
  );
};

export default EnterLeft;
