import React, { useState, useEffect } from 'react';
import useWebSocket  from 'react-use-websocket';

const TagForm = () => {
  const [tagName, setTagName] = useState('');
  const [registrationFormVisible, setRegistrationFormVisible] = useState(false);
  const [currentTag, setCurrentTag] = useState('');

  const socketUrl = 'ws://localhost:8082';
  const { lastJsonMessage, sendMessage } = useWebSocket(socketUrl);

  const handleRegistrationSubmit = (event) => {
    event.preventDefault();
    sendMessage(`${tagName}`);
    setRegistrationFormVisible(false);
  };

  // Effect to handle incoming WebSocket messages
  useEffect(() => {
    if (lastJsonMessage) {
      const data = lastJsonMessage;
      if (data.tag === 'requestName') {
        setRegistrationFormVisible(true);
      } else {
        setCurrentTag(`Current RFID Tag: ${data.tag}`);
      }
      
    }
    
  }, [lastJsonMessage]);
 

  return (
    <div>
      <h1>RFID Tags Viewer</h1>

      <form id="registrationForm" style={{ display: registrationFormVisible ? 'block' : 'none' }} onSubmit={handleRegistrationSubmit}>
        <label htmlFor="tagName">Nome:</label>
        <input type="text" id="tagName" placeholder="Digite seu nome" value={tagName} onChange={(e) => setTagName(e.target.value)} />
        <input type="submit" value="Registrar" />
      </form>

      <p id="currentTag">{currentTag}</p>
    </div>
  );
};

export default TagForm;
