import React, { useState, useEffect } from 'react';
import axios from 'axios';
import useWebSocket from 'react-use-websocket';

const EnterLeft = () => {
  const [id_user, setIdUser] = useState('');
  const [tag, setTag] = useState('');


  const socketUrl = 'ws://localhost:8082/';
  const { lastMessage} = useWebSocket(socketUrl);

  useEffect(() => {
    if (lastMessage !== null) {
      const data = JSON.parse(lastMessage.data);
      setIdUser(data.id_user);
      setTag(data.tag);
    }
  }, [lastMessage]);

  const handleRegisterEntry = async () => {
    try {
      const response = await axios.post('http://localhost:8082/register/', { id_user, tag });
      console.log(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleRegisterExit = async () => {
    try {
      const response = await axios.patch('http://localhost:8082/register/', { id_user, tag });
      
      if (response.data.success) {
        console.log(response.data);
      } else {
        console.error('Error:', response.data.message);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Registrar Entrada</h2>
      <label>ID do Usuário:</label>
      <input type="text" value={id_user} onChange={(e) => setIdUser(e.target.value)} />

      <label>Tag:</label>
      <input type="text" value={tag} onChange={(e) => setTag(e.target.value)} />

      <button onClick={handleRegisterEntry}>Registrar Entrada</button>

      <hr />

      <h2>Registrar Saída</h2>
      <label>ID do Usuário:</label>
      <input type="text" value={id_user} onChange={(e) => setIdUser(e.target.value)} />

      <label>Tag:</label>
      <input type="text" value={tag} onChange={(e) => setTag(e.target.value)} />

      <button onClick={handleRegisterExit}>Registrar Saída</button>
    </div>
  );
};

export default EnterLeft;
