import React, { useState, useEffect } from 'react';
import { ReadyState } from 'react-use-websocket';

const TagForm = () => {
  const [tag, setTag] = useState('');

  // Substitua pela URL do seu servidor WebSocket
  const socketUrl = 'ws://localhost:8082';

  const { sendJsonMessage, lastJsonMessage, readyState } = useWebSocket(socketUrl);

  useEffect(() => {
    if (readyState === ReadyState.OPEN) {
      // Envia uma mensagem vazia para o servidor WebSocket
      sendJsonMessage({});
    }
  }, [readyState, sendJsonMessage]);

  useEffect(() => {
    if (lastJsonMessage && lastJsonMessage.tag) {
      setTag(lastJsonMessage.tag);
    }
  }, [lastJsonMessage]);

  return (
    <div>
      <h1>Current RFID Tag: {tag}</h1>
    </div>
  );
};

export default TagForm;
