import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TagForm = () => {
  const [tag, setTag] = useState('');
  const [result, setResult] = useState('');

  useEffect(() => {
    const handleRFIDRead = (event) => {
      const rfidValue = event.detail.tag;
      setTag(rfidValue);
    };

    document.addEventListener('RFIDRead', handleRFIDRead);

    return () => {
      document.removeEventListener('RFIDRead', handleRFIDRead);
    };
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.get('http://localhost:8082/rota', {
        params: {
          tag: tag,
        },
      });

      setResult(response.data);

      if (response.data === 'ID da tag RFID recebido e adicionado ao banco de dados com sucesso.') {
        // Se a tag foi adicionada com sucesso, emita um evento para atualizar o estado da tag
        const RFIDReadEvent = new CustomEvent('RFIDRead', { detail: { tag: tag } });
        document.dispatchEvent(RFIDReadEvent);
      }
    } catch (error) {
      console.error('Erro ao fazer a solicitação:', error);
      setResult('Erro ao fazer a solicitação.');
    }
  };

  return (
    <div>
      <h1>Front-end para Rota</h1>

      <form onSubmit={handleSubmit}>
        <label htmlFor="tagInput">Tag RFID:</label>
        <input
          type="text"
          id="tagInput"
          name="tag"
          value={tag}
          onChange={(e) => setTag(e.target.value)}
          required
        />
        <button type="submit">Enviar</button>
      </form>

      <div>{result}</div>
    </div>
  );
};

export default TagForm;
