// src/components/TagForm.js
import React, { useState } from 'react';
import axios from 'axios';

const TagForm = ({ onTagSubmit }) => {
  const [tagId, setTagId] = useState('');
  const [tagName, setTagName] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Enviar dados para o servidor usando Axios
      const response = await axios.post('http://localhost:8082/associate-tag', {
        tag: tagId,
        name: tagName,
      });

      console.log(response.data); // Dados retornados pelo servidor

      // Atualizar o estado local, se necessário
      onTagSubmit(response.data);
    } catch (error) {
      console.error('Error associating tag with name:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="tagId">ID da Tag:</label>
      <input type="text" id="tagId" value={tagId} readOnly />
      <br />
      <label htmlFor="tagName">Nome:</label>
      <input
        type="text"
        id="tagName"
        value={tagName}
        onChange={(e) => setTagName(e.target.value)}
      />
      <br />
      <button type="submit">Associar Nome</button>
    </form>
  );
};

export default TagForm;
