// TagView.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';

const TagView = () => {
  const [tags, setTags] = useState([]);
  const [selectedTag, setSelectedTag] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:8082/tags/')
      .then(response => {
        setTags(response.data);
      })
      .catch(error => {
        console.error('Error fetching tags:', error);
      });
  }, []);

  const openModal = (tag) => {
    setSelectedTag(tag);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setSelectedTag(null);
    setModalIsOpen(false);
  };

  return (
    <div className="my-8">
      <h2 className="text-2xl font-bold mb-4">Tags Cadastradas</h2>
      <ul className="list-disc pl-4">
        {tags.map(tag => (
          <li key={tag.id} className="text-lg cursor-pointer" onClick={() => openModal(tag)}>
            {tag.tag}
          </li>
        ))}
      </ul>

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Informações da Tag"
        className="modal bg-white p-6 rounded-lg shadow-lg mx-auto my-32 max-w-screen-md"
        overlayClassName="overlay fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center"
      >
        <div className="modal-content">
          <h2 className="text-2xl font-bold mb-4">Informações da Tag</h2>
          {selectedTag && (
            <div>
              <p>ID: {selectedTag.id}</p>
              <p>Tag: {selectedTag.tag}</p>
              {/* Adicione outras informações aqui conforme necessário */}
            </div>
          )}
          <button onClick={closeModal} className="mt-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
            Fechar Modal
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default TagView;
