import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TagForm = () => {
  const [tagInput, setTagInput] = useState('');
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const newSocket = new WebSocket('ws://localhost:8082'); // Substitua pelo URL real do seu servidor WebSocket
    setSocket(newSocket);

    newSocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('Received message:', data);

      // Atualizar automaticamente o campo de entrada quando uma tag é recebida
      setTagInput(data.tag);
    };

    return () => {
      newSocket.close();
    };
  }, []);

  const handleInputChange = (event) => {
    setTagInput(event.target.value);
  };

  const sendTag = async () => {
    try {
      if (tagInput.trim() !== '') {
        const response = await axios.post('http://localhost:8082/api/tags', {
          tag: tagInput,
        });

        console.log('Tag created successfully:', response.data);

        // Limpar o campo de entrada após o envio
        setTagInput('');
      }
    } catch (error) {
      console.error('Error creating tag:', error.message);
    }
  };

  return (
    <div>
      <h1>Create Tag (React)</h1>
      <form>
        <label htmlFor="tagInput">Tag:</label>
        <input
          type="text"
          id="tagInput"
          value={tagInput}
          onChange={handleInputChange}
          required
        />
        <button type="button" onClick={sendTag}>
          Create Tag
        </button>
      </form>
    </div>
  );
};

export default TagForm;
