import React, { useEffect, useState } from 'react';
import  useWebSocket  from 'react-use-websocket';

const TagForm = () => {
  const [sendMessage] = useWebSocket("ws://localhost:8082/");
  const [lastMessage, setLastMessage] = useState(null);
  const [currentTag, setCurrentTag] = useState('Loading...');
  const [currentName, setCurrentName] = useState('');
  const [timestamp, setTimestamp] = useState('Loading...');

  useEffect(() => {
    if (lastMessage !== null) {
      const data = JSON.parse(lastMessage.data);

      if (data.tag === 'requestName') {
        // Se o servidor solicitar um nome, você pode adicionar lógica aqui para responder
        // por exemplo: sendMessage(JSON.stringify({ name: 'seu_nome' }));
      } else {
        setCurrentTag(`Current RFID Tag: ${data.tag}`);
        setCurrentName(`Name: ${data.name}`);
        if (data.timestamp) {
          setTimestamp(`Timestamp: ${data.timestamp}`);
        }
      }
    }
  }, [lastMessage]);

  return (
    <div>
      <h1>RFID Tags Viewer</h1>
      <p id="currentTag">{currentTag}</p>
      <p id="currentName">{currentName}</p>
      <p id="timestamp">{timestamp}</p>

      <form
        onSubmit={(e) => {
          e.preventDefault();
          const name = e.target.elements.tagName.value;
          // Envie o nome associado à tag RFID
          sendMessage(JSON.stringify({ name }));
        }}
      >
        <label htmlFor="tagName">Tag Name:</label>
        <input type="text" id="tagName" />
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default TagForm;
