import React, { useState, useEffect } from 'react';
import { useWebSocket, ReadyState } from 'react-use-websocket';

const TagForm = () => {
  const [tag, setTag] = useState('');
  const [socketUrl] = useState('ws://localhost:8082'); // Use a URL correta do seu servidor WebSocket

  const { sendJsonMessage, readyState, lastMessage } = useWebSocket(socketUrl);

  useEffect(() => {
    if (readyState === ReadyState.OPEN) {
      // Envia uma mensagem vazia para o servidor WebSocket
      sendJsonMessage({});
    }
  }, [readyState, sendJsonMessage]);

  useEffect(() => {
    if (lastMessage) {
      try {
        const data = JSON.parse(lastMessage.data);
        const receivedTag = data.tag;
        setTag(receivedTag);
      } catch (error) {
        console.error('Erro ao processar a mensagem:', error);
      }
    }
  }, [lastMessage]);

  return (
    <div>
      <h1>Tag RFID Atual: {tag}</h1>
      {/* Outros componentes ou l√≥gica podem ser adicionados aqui */}
    </div>
  );
};

export default TagForm;
